import React from "react";
import dateFns from "date-fns";
import './Calendar.css';
import firebase from '../firebase';
import { format } from "path";

class Calendar extends React.Component {
  
  constructor(props) {
    super(props);
    this.state = {
      currentMonth: new Date(),
      selectedDate: new Date(),
      eventDescription: null,
    };
    this.renderEvents = this.renderEvents.bind(this);
  }

  renderHeader() {
    
    const dateFormat = "MMMM YYYY";

    return (
      <div className="header row flex-middle">
        <div className="col col-start">
          <div className="icon" onClick={this.prevMonth}>chevron_left</div>
        </div>
        <div className="col col-center">
          <span>{dateFns.format(this.state.currentMonth, dateFormat)}</span>
        </div>
        <div className="col col-end">
          <div className="icon" onClick={this.nextMonth}>chevron_right</div>
        </div>
      </div>
    );

  }

  renderDays() {

    const dateFormat = "ddd";
    const days = [];

    let startDate = dateFns.startOfWeek(this.state.currentMonth);

    for (let i = 0; i < 7; i++) {
      days.push (
        <div className="col col-center" key={i}>
          {dateFns.format(dateFns.addDays(startDate, i), dateFormat)}
        </div>
      );
    }

    return <div className="days row">{days}</div>;
  }

  renderCells() {

    const { currentMonth, selectedDate } = this.state;

    const monthStart = dateFns.startOfMonth(currentMonth);
    const monthEnd = dateFns.endOfMonth(monthStart);
    const startDate = dateFns.startOfWeek(monthStart);
    const endDate = dateFns.endOfWeek(monthEnd);

    const dateFormat = "D";
    const rows = [];

    let days = [];
    let day = startDate;
    let formattedDate = "";

    while (day <= endDate) {

      for (let i = 0; i < 7; i++) {

        formattedDate = dateFns.format(day, dateFormat);
        const cloneDay = day;
        
        days.push (
          <div
            className={`col cell ${!dateFns.isSameMonth(day, monthStart) ? "disabled" : dateFns.isSameDay(day, selectedDate) ? "selected" : ""}`}
            key={day}
            onClick={() => this.onDateClick(dateFns.parse(cloneDay))}
          >
            <span className="number">{formattedDate}</span>
          </div>
        );

        day = dateFns.addDays(day, 1);
      }

      rows.push (
        <div className="row" key={day}>{days}</div>
      );

      days = [];
    }

    return <div className="body">{rows}</div>;
  }

  renderEvents(event) {
    event.preventDefault();
    var db = firebase.firestore();
    db.collection("events")
    .where("eventStart", "==", this.state.selectedDate)
    .get()
    .then(function(querySnapshot) {
      querySnapshot.forEach(function(doc) {
        console.log(doc.id, "=> ", doc.data());
        this.setState({
          eventDescription: doc.data()['eventDescription']
        })
      });
    })
    .catch(function(error) {
      console.log(error);
    });
    return () => (
      <div>
        <span className="event">{this.state.eventDescription}</span>
      </div>
    );
  };

  onDateClick = day => {
    this.setState ({
      selectedDate: day
    });
  };

  nextMonth = () => {
    this.setState ({
      currentMonth: dateFns.addMonths(this.state.currentMonth, 1)
    });
  };

  prevMonth = () => {
    this.setState ({
      currentMonth: dateFns.subMonths(this.state.currentMonth, 1)
    });
  };

  render() {
    return (
      <div className="calendar">
        {this.renderHeader()}
        {this.renderDays()}
        {this.renderCells()}
        {this.renderEvents}
      </div>
    );
  }
}

export default Calendar;
